import { ofetch } from "ofetch";
import { joinURL } from "ufo";
import { requireNuxtHubFeature } from "../../../utils/features.js";
export function proxyHubCache(projectUrl, secretKey) {
  requireNuxtHubFeature("cache");
  const cacheAPI = ofetch.create({
    baseURL: joinURL(projectUrl, "/api/_hub/cache"),
    headers: {
      Authorization: `Bearer ${secretKey}`
    }
  });
  const cache = {
    async list() {
      return cacheAPI("/", {
        method: "GET"
      });
    },
    async get(key) {
      return cacheAPI(`/${key}`, {
        method: "GET"
      });
    },
    async del(key) {
      await cacheAPI(`/${key}`, {
        method: "DELETE"
      });
      return;
    },
    async clear(base) {
      await cacheAPI(`/clear/${base}`, {
        method: "DELETE"
      });
      return;
    },
    async batchDel(keys) {
      await cacheAPI("/batch-delete", {
        method: "POST",
        body: { keys }
      });
      return;
    }
  };
  return cache;
}
